// apps/admin/frontend/src/pages/Dashboard.tsx

"use client"

import React from 'react';
import { Link } from 'react-router-dom';
import { 
  Users, // Total Pasien
  Zap, // Penerimaan Gawat Darurat
  Heart, // O- Satuan Darah
  Hospital, // Tempat Tidur Tersedia
  Loader2, // Loading spinner
  Activity, // Untuk PatientPerHour
  Calendar, // Untuk PatientPerWeek / Jadwal
  Package // Untuk Stok Rendah
} from 'lucide-react';

// Import komponen kustom yang sesuai untuk desain admin
import MetricCard from '../components/Dashboard/MetricCard'; // Sudah ada
import HealthTrendChart from '../components/charts/HealthTrendChart'; // Sudah ada
import HealthMetricsChart from '../components/charts/HealthMetricsChart'; // Sudah ada
import AIInsightCard from '../components/Dashboard/AIInsightCard'; // Sudah ada
import SystemInfoCard from '../components/Dashboard/SystemInfoCard'; // Sudah ada
import QuickActionsCard from '../components/Dashboard/QuickActionsCard'; // Sudah ada

import { useAuth } from '../contexts/AuthContext'; // Admin AuthContext
import { useQuery, useMutation } from '@tanstack/react-query'; // Menggunakan useMutation untuk seed
import toast from 'react-hot-toast'; // Untuk notifikasi

// Import API services dan tipe yang diperlukan dari services/api
import { 
  dashboardAPI, 
  inventoryAPI, // Untuk stok darah
  patientAPI, // Untuk total pasien
  polyclinicAPI, // Untuk data poliklinik
  type DashboardOverviewApiData, 
  type ChartDataForRecharts, 
  type ApiResponse 
} from '../services/api'; 

const Dashboard: React.FC = () => {
  const { user, isAuthenticated, isLoading: isLoadingAuth, refreshUser } = useAuth();

  // Ini untuk refresh profil admin, jika perlu nama yang benar
  React.useEffect(() => {
    if (isAuthenticated && user && !user.name) { // User admin punya properti 'name'
      refreshUser(); 
    }
  }, [isAuthenticated, user, refreshUser]);

  // Query untuk Dashboard Overview (sesuai desain)
  const { data: adminOverviewResponse, isLoading: isLoadingAdminOverview } = useQuery<ApiResponse<DashboardOverviewApiData>>({
    queryKey: ['adminDashboardOverview'],
    queryFn: () => dashboardAPI.getAdminDashboardOverview(), // Endpoint baru di backend
  });
  const adminOverviewData = adminOverviewResponse?.data;

  // Query untuk Pasien Minggu Ini (Line Chart)
  const { data: patientsPerWeekResponse, isLoading: isLoadingPatientsPerWeek } = useQuery<ApiResponse<ChartDataForRecharts[]>>({
    queryKey: ['patientsPerWeek'],
    queryFn: () => dashboardAPI.getPatientsPerWeek(), // Endpoint baru di backend
  });
  const patientsPerWeekData = patientsPerWeekResponse?.data || [];

  // Query untuk Pasien per Jam (Bar Chart)
  const { data: patientsPerHourResponse, isLoading: isLoadingPatientsPerHour } = useQuery<ApiResponse<ChartDataForRecharts[]>>({
    queryKey: ['patientsPerHour'],
    queryFn: () => dashboardAPI.getPatientsPerHour(), // Endpoint baru di backend
  });
  const patientsPerHourData = patientsPerHourResponse?.data || [];

  // Query untuk AI Insights
  const { data: aiInsightsResponse, isLoading: isLoadingAIInsights } = useQuery<ApiResponse<any>>({ // Ganti any dengan tipe AI Insight yang lebih spesifik
    queryKey: ['aiInsights'],
    queryFn: () => dashboardAPI.getAIInsights(), // Endpoint baru di backend
  });
  const aiInsightSummary = aiInsightsResponse?.data?.summary;
  const aiRecommendations = aiInsightsResponse?.data?.recommendations || [];


  // Data Serapan Hari Ini (Ini mungkin kombinasi dari data lain atau endpoint baru)
  // Untuk saat ini, kita bisa mock data ini atau mendapatkan dari API lain
  const { data: lowStockAlertsResponse, isLoading: isLoadingLowStock } = useQuery<ApiResponse<any>>({
    queryKey: ['lowStockAlerts'],
    queryFn: () => inventoryAPI.getLowStockAlerts(), // Menggunakan API inventaris yang sudah ada
  });
  const lowStockCount = lowStockAlertsResponse?.data?.summary?.total || 0;


  // Data untuk "Serapan Hari Ini" (gabungan dari berbagai sumber)
  const todayAbsorption = React.useMemo(() => {
    // Anda bisa mendapatkan ini dari dashboardAPI atau menggabungkan dari berbagai API lain
    // Untuk demo, kita akan mock atau menggunakan data yang sudah ada
    return [
      { id: '1', text: `Stok darah O- rendah (${adminOverviewData?.bloodUnitsOminus || 0} unit tersedia)`, priority: "high" }, // Dari overview
      { id: '2', text: `Pasien meningkat ${adminOverviewData?.patientTrendData?.[0]?.value || 0}% dibandingkan kemarin`, priority: "medium" }, // Dari trend data (perlu logika perhitungan perbandingan kemarin)
      { id: '3', text: `Shift malam perlu tambahan perawat`, priority: "low" }, // Contoh saran manual
    ];
  }, [adminOverviewData]); // Bergantung pada data overview

  // Data Sistem Health (SystemInfoCard)
  const { data: systemHealthResponse, isLoading: isLoadingSystemHealth } = useQuery<ApiResponse<any>>({
    queryKey: ['systemHealth'],
    queryFn: () => dashboardAPI.getSystemHealth(), // Menggunakan API system health yang sudah ada
  });
  const systemHealthData = systemHealthResponse?.data;


  // Mutasi untuk Seed Database (dari QuickActionsCard)
  const seedDatabaseMutation = useMutation({
    mutationFn: async () => {
      // Anda perlu membuat endpoint POST /api/v1/seed/database di backend
      // Contoh: return api.post('/seed/database');
      toast.error("Seed Database API belum diimplementasikan di backend!");
      return Promise.reject("Not Implemented"); // Placeholder
    },
    onSuccess: () => {
      toast.success("Database berhasil diisi dengan data sampel!");
      // Invalidate semua query terkait dashboard agar data baru termuat
      queryClient.invalidateQueries({ queryKey: ['adminDashboardOverview'] });
      queryClient.invalidateQueries({ queryKey: ['patientsPerWeek'] });
      queryClient.invalidateQueries({ queryKey: ['patientsPerHour'] });
      queryClient.invalidateQueries({ queryKey: ['aiInsights'] });
      queryClient.invalidateQueries({ queryKey: ['lowStockAlerts'] });
      queryClient.invalidateQueries({ queryKey: ['systemHealth'] });
    },
    onError: (error: any) => {
      toast.error(`Gagal mengisi database: ${error.response?.data?.message || error.message}`);
    },
  });

  const overallLoading = isLoadingAdminOverview || isLoadingPatientsPerWeek || isLoadingPatientsPerHour || isLoadingAIInsights || isLoadingLowStock || isLoadingSystemHealth || seedDatabaseMutation.isPending;

  // Jika belum login atau masih loading autentikasi, mungkin tampilkan loader atau redirect
  if (isLoadingAuth) {
    return (
      <div className="flex h-screen items-center justify-center">
        <Loader2 className="h-10 w-10 animate-spin text-primary" />
        <p className="ml-4 text-text-light">Memuat informasi autentikasi...</p>
      </div>
    );
  }

  // Jika tidak terautentikasi (admin), arahkan ke login
  if (!isAuthenticated && !isLoadingAuth) {
    return (
      <div className="min-h-screen bg-background py-8 flex items-center justify-center text-center">
        <p className="text-xl text-text-light">Silakan <Link to="/login" className="text-primary hover:underline">login</Link> untuk melihat dashboard Anda.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6"> {/* Tambah padding */}
      {/* Header Dashboard */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-slate-800">Dashboard Overview</h1>
        <p className="text-slate-500">Selayang pandang manajemen rumah sakit Anda.</p>
      </div>

      {/* Metric Cards (Baris 1) */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-5">
        <MetricCard title="Total Pasien" value={adminOverviewData?.totalPatients?.toLocaleString('id-ID') || '0'} icon={Users} color="blue" trend="up" trendValue="+12%" />
        <MetricCard title="Penerimaan Gawat Darurat" value={adminOverviewData?.erAdmissions?.toLocaleString('id-ID') || '0'} icon={Zap} color="purple" trend="up" trendValue="+17%" />
        <MetricCard title="O- Satuan Darah" value={adminOverviewData?.bloodUnitsOminus?.toLocaleString('id-ID') || '0'} icon={Heart} color="red" description="Stok Rendah" />
        <MetricCard title="Tempat Tidur Tersedia" value={adminOverviewData?.availableBeds?.toLocaleString('id-ID') || '0'} icon={Hospital} color="green" description="Tersedia" />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Pasien Minggu Ini (Line Chart) */}
        <div className="lg:col-span-2">
          <HealthTrendChart title="Pasien Minggu Ini" data={patientsPerWeekData} dataKey="value" strokeColor="#3B82F6" />
        </div>

        {/* AI Assistant Card (kolom paling kanan) */}
        <AIInsightCard 
          summary={adminOverviewData?.aiInsightSummary || "Selamat datang di AI Assistant Anda. Bagaimana saya bisa membantu Anda hari ini?"} 
          recommendations={adminOverviewData?.aiRecommendations || []} 
          isLoading={isLoadingAIInsights}
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Pasien per Jam (Bar Chart) */}
        <div className="lg:col-span-2">
          <HealthMetricsChart title="Pasien per Jam" type="bar" data={patientsPerHourData} />
        </div>

        {/* System Info Card (di bawah AI Assistant) */}
        <SystemInfoCard healthData={systemHealthResponse?.data} isLoading={isLoadingSystemHealth} />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Quick Actions Card (Serapan Hari Ini) */}
        <div className="lg:col-span-2">
          <QuickActionsCard onSeedDatabase={seedDatabaseMutation.mutate} isSeeding={seedDatabaseMutation.isPending} />
        </div>

        {/* Serapan Hari Ini (di bawah System Info Card) */}
        <AIInsightCard 
          summary="Serapan Hari Ini" // Ubah ini sesuai konteks
          recommendations={todayAbsorption} // Gunakan data serapan
          isLoading={overallLoading} // Sesuaikan loading
        />
      </div>
    </div>
  )
}

export default Dashboard;
