// apps/admin/frontend/src/pages/Dashboard.tsx

import React from 'react';
import { Link } from 'react-router-dom';
import { 
  Calendar, 
  MessageCircle, 
  FileText, 
  Heart, 
  User, 
  Clock, 
  Activity,
  Bell,
  Loader2 
} from 'lucide-react';
// PERBAIKAN: Pastikan path Card dan Button benar
import Card from '../components/Dashboard/Card'; // Asumsi Card ada di components/Dashboard/Card.tsx
import Button from '../components/Dashboard/Button'; // Asumsi Button ada di components/Dashboard/Button.tsx
import { useAuth } from '../contexts/AuthContext';
import { useQuery } from '@tanstack/react-query';
// PERBAIKAN: Impor API services dan tipe dengan benar dari services/api
import { 
  appointmentAPI, 
  medicalRecordAPI, 
  patientAPI, // Menggunakan patientAPI karena patientAuthAPI tidak diekspor global di admin/frontend/api.ts
  dashboardAPI, 
  type Appointment, 
  type MedicalRecord, 
  type ApiResponse, // Impor ApiResponse
  type DashboardOverviewApiData, 
  type ChartDataForRecharts, 
  type ServiceDistributionData 
} from '../services/api'; 


// Import komponen chart yang benar
import HealthTrendChart from '../components/charts/HealthTrendChart'; 
import HealthMetricsChart from '../components/charts/HealthMetricsChart'; 


const Dashboard: React.FC = () => {
  const { user, isAuthenticated, isLoading: isLoadingAuth, refreshUser } = useAuth();

  React.useEffect(() => {
    // Pastikan user memiliki fullName setelah refresh atau login
    // 'user' dari AuthContext di admin/frontend adalah IUser, yang punya 'name' bukan 'fullName'
    // Jika Anda menggunakan user 'admin' di dashboard ini, gunakan user.name
    // Jika Anda ingin user 'patient' masuk ke dashboard ini (seperti POV user),
    // maka Anda perlu memastikan 'user' di AuthContext (admin) punya 'fullName' atau gunakan nama yang sesuai.
    // Untuk saat ini, saya asumsikan user yang login ke admin dashboard adalah IUser (dengan 'name')
    if (isAuthenticated && user && !(user as any).name && (user as any).role !== 'doctor') { // Cek user.name
      refreshUser(); 
    }
  }, [isAuthenticated, user, refreshUser]);

  const dashboardCards = [
    {
      icon: Calendar,
      title: 'Book Appointment',
      description: 'Schedule a visit with your preferred doctor',
      link: '/book-appointment',
      color: 'bg-blue-500'
    },
    {
      icon: MessageCircle,
      title: 'AI Health Assistant',
      description: 'Get instant health advice and symptom assessment',
      link: '/chatbot',
      color: 'bg-green-500'
    },
    {
      icon: FileText,
      title: 'Medical Records',
      description: 'View your complete medical history',
      link: '/medical-records',
      color: 'bg-purple-500'
    },
    {
      icon: Heart,
      title: 'Health Articles',
      description: 'Read expert health tips and articles',
      link: '/articles',
      color: 'bg-red-500'
    }
  ];

  // patientId hanya relevan jika dashboard ini untuk pasien yang login
  // Di admin dashboard, biasanya tidak ada patientId tunggal
  // Untuk tujuan demo, kita bisa mock atau mendapatkan ID pasien pertama jika diperlukan
  const patientId = (user as any)?._id; // Jika user yang login adalah admin, maka patientId ini tidak relevan

  // Query untuk profil user admin (bukan pasien)
  // Untuk mendapatkan nama "Administrator" atau "John", ini berasal dari `authAPI.getProfile()`
  const { data: profileResponse, isLoading: isLoadingProfile } = useQuery<ApiResponse<User>>({ // Tipe User dari ../types
    queryKey: ['adminProfile', user?._id], // Key unik untuk profil admin
    queryFn: () => authAPI.getProfile(), // Menggunakan authAPI untuk profil admin
    enabled: isAuthenticated && !!user?._id, // Hanya jalankan jika authenticated dan user id ada
    staleTime: 5 * 60 * 1000, 
  });
  const adminProfile = profileResponse?.data?.user;

  // Query untuk Upcoming Appointments
  const { data: upcomingAppointmentsResponse, isLoading: isLoadingAppointments } = useQuery<ApiResponse<Appointment[]>>({ // Tipe Appointment[]
    queryKey: ['upcomingAppointments', patientId], // Jika ini untuk pasien yang login
    queryFn: async () => {
      // Endpoint ini mungkin tidak relevan untuk admin dashboard
      // Jika ini dashboard admin, Anda mungkin ingin daftar semua janji temu
      // Bukan janji temu spesifik pasien
      console.warn("API for upcoming appointments in Admin Dashboard is not yet fully implemented. Using mock data.");
      return new Promise(resolve => setTimeout(() => resolve({
        success: true,
        data: [
          {
            _id: 'mock-1',
            queueId: 'Q001',
            patientId: 'some-patient-id', // Perlu ID pasien yang valid
            doctorId: 'some-doctor-id', 
            polyclinicId: 'some-polyclinic-id', 
            scheduleId: 'some-schedule-id', 
            queueNumber: 15,
            queueDate: new Date(Date.now() + 2 * 24 * 60 * 60 * 1000).toISOString(), 
            appointmentTime: '09:00',
            status: 'Waiting',
            // Doctor dan Polyclinic mungkin perlu di-populate di backend
          }
        ]
      }), 500));
    },
    enabled: isAuthenticated && !!patientId, // Hanya aktif jika ada patientId (relevan untuk POV pasien)
  });
  const upcomingAppointments = upcomingAppointmentsResponse?.data || [];
  const nextAppointment = upcomingAppointments.sort((a, b) => new Date(a.queueDate).getTime() - new Date(b.queueDate).getTime())[0]; 

  // Query untuk Health Records (total rekam medis)
  const { data: healthRecordsResponse, isLoading: isLoadingHealthRecords } = useQuery<ApiResponse<MedicalRecord[]>>({ 
    queryKey: ['totalHealthRecords'], // Key lebih umum untuk admin
    queryFn: async () => {
      // Ini bisa berupa endpoint /visits/total atau /medical-records/total di backend
      console.warn("API for total medical records in Admin Dashboard is not yet fully implemented. Using mock data.");
      return new Promise(resolve => setTimeout(() => resolve({
        success: true,
        data: [
          { id: 'rec-1', patientId: 'p1', visitDate: '2024-06-10', doctorName: 'Dr. Anya Taylor', diagnosis: 'Common Cold', treatments: ['Rest'], prescriptions: ['Paracetamol'] },
        ]
      }), 500));
    },
    enabled: isAuthenticated, // Aktif jika admin login
  });
  const healthRecords = healthRecordsResponse?.data || [];


  // Data Aktivitas Terbaru (menggunakan tipe yang benar)
  const displayRecentActivities = React.useMemo(() => {
    const activities: { type: string, message: string, time: string, icon: React.ElementType }[] = [];

    upcomingAppointments.forEach(app => {
      activities.push({
        type: 'appointment',
        message: `Upcoming appointment with ${(app as any).doctor?.name || 'Dr.'} (${(app as any).polyclinic?.name || 'Poli'})`, 
        time: `On ${new Date(app.queueDate).toLocaleDateString('id-ID', {day: 'numeric', month: 'short'})} at ${app.appointmentTime}`,
        icon: Calendar
      });
    });

    healthRecords.forEach((rec: MedicalRecord) => { 
      activities.push({
        type: 'record',
        message: `New medical record from visit on ${new Date(rec.visitDate).toLocaleDateString('id-ID', {day: 'numeric', month: 'short'})}`,
        time: `Visited ${rec.doctorName}`,
        icon: FileText
      });
    });

    if (activities.length === 0) {
      activities.push({ type: 'article', message: 'Read our latest article: "Healthy Living Tips"', time: '1 day ago', icon: Heart });
      activities.push({ type: 'welcome', message: 'Welcome to Sehatify! Explore our features.', time: 'Just now', icon: User });
    }

    return activities.slice(0, 5); 
  }, [upcomingAppointments, healthRecords]);


  const overallLoading = isLoadingAuth || isLoadingProfile || isLoadingAppointments || isLoadingHealthRecords;

  if (isLoadingAuth) {
    return (
      <div className="flex h-screen items-center justify-center">
        <Loader2 className="h-10 w-10 animate-spin text-primary" />
        <p className="ml-4 text-text-light">Memuat informasi user...</p>
      </div>
    );
  }

  // Jika tidak terautentikasi (admin), arahkan ke login
  if (!isAuthenticated && !isLoadingAuth) {
    return (
      <div className="min-h-screen bg-background py-8 flex items-center justify-center text-center">
        <p className="text-xl text-text-light">Silakan <Link to="/login" className="text-primary hover:underline">login</Link> untuk melihat dashboard Anda.</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-text">Welcome back, {adminProfile?.name || adminProfile?.email || 'Administrator'}!</h1> {/* Gunakan adminProfile.name */}
              <p className="text-text-light mt-1">Here's what's happening with your health today</p>
            </div>
            <div className="flex items-center space-x-4">
              <Button variant="outline" size="sm">
                <Bell className="h-4 w-4 mr-2" />
                Notifications
              </Button>
              <div className="flex items-center space-x-3">
                <img
                  src="https://images.pexels.com/photos/2379005/pexels-photo-2379005.jpeg?auto=compress&cs=tinysrgb&w=150"
                  alt="Profile"
                  className="h-10 w-10 rounded-full object-cover"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card>
            <div className="flex items-center">
              <div className="bg-primary/10 rounded-full p-3">
                <Calendar className="h-8 w-8 text-primary" />
              </div>
              <div className="ml-4">
                {overallLoading ? <Loader2 className="h-6 w-6 animate-spin text-primary" /> : <p className="text-2xl font-bold text-text">{upcomingAppointments.length}</p>}
                <p className="text-text-light">Upcoming Appointments</p>
              </div>
            </div>
          </Card>

          <Card>
            <div className="flex items-center">
              <div className="bg-green-100 rounded-full p-3">
                <Activity className="h-8 w-8 text-green-600" />
              </div>
              <div className="ml-4">
                {overallLoading ? <Loader2 className="h-6 w-6 animate-spin text-green-600" /> : <p className="text-2xl font-bold text-text">{healthRecords.length}</p>}
                <p className="text-text-light">Health Records</p>
              </div>
            </div>
          </Card>

          <Card>
            <div className="flex items-center">
              <div className="bg-blue-100 rounded-full p-3">
                <Clock className="h-8 w-8 text-blue-600" />
              </div>
              <div className="ml-4">
                {overallLoading ? <Loader2 className="h-6 w-6 animate-spin text-blue-600" /> : <p className="text-2xl font-bold text-text">0</p>} 
                <p className="text-text-light">Pending Results</p>
              </div>
            </div>
          </Card>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Dashboard Cards */}
          <div className="lg:col-span-2">
            <h2 className="text-2xl font-bold text-text mb-6">Quick Actions</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {dashboardCards.map((card, index) => (
                <Link key={index} to={card.link}>
                  <Card hover className="h-full">
                    <div className="flex items-center mb-4">
                      <div className={`${card.color} rounded-full p-3 text-white`}>
                        <card.icon className="h-8 w-8" />
                      </div>
                    </div>
                    <h3 className="text-xl font-semibold text-text mb-2">{card.title}</h3>
                    <p className="text-text-light">{card.description}</p>
                  </Card>
                </Link>
              ))}
            </div>
          </div>

          {/* Recent Activity */}
          <div>
            <h2 className="text-2xl font-bold text-text mb-6">Recent Activity</h2>
            <Card>
              <div className="space-y-4">
                {overallLoading ? (
                  <div className="flex justify-center items-center py-4">
                    <Loader2 className="h-6 w-6 animate-spin text-primary" />
                  </div>
                ) : displayRecentActivities.length > 0 ? (
                  displayRecentActivities.map((activity, index) => (
                    <div key={index} className="flex items-start space-x-3">
                      <div className="bg-primary/10 rounded-full p-2">
                        <activity.icon className="h-5 w-5 text-primary" />
                      </div>
                      <div className="flex-1">
                        <p className="text-text font-medium">{activity.message}</p>
                        <p className="text-text-light text-sm">{activity.time}</p>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-text-light text-center py-4">Tidak ada aktivitas terbaru.</p>
                )}
              </div>
            </Card>

            {/* Next Appointment */}
            <Card className="mt-6">
              <h3 className="text-lg font-semibold text-text mb-4">Next Appointment</h3>
              {overallLoading ? (
                <div className="flex justify-center items-center py-4">
                  <Loader2 className="h-6 w-6 animate-spin text-primary" />
                </div>
              ) : nextAppointment ? (
                <div className="bg-primary/5 rounded-lg p-4">
                  <div className="flex items-center space-x-3">
                    <img
                      src="https://images.pexels.com/photos/5327921/pexels-photo-5327921.jpeg?auto=compress&cs=tinysrgb&w=100" 
                      alt={nextAppointment.doctor?.name || "Doctor"}
                      className="h-12 w-12 rounded-full object-cover"
                    />
                    <div>
                      <p className="font-medium text-text">{nextAppointment.doctor?.name || 'Dokter Tidak Dikenal'}</p>
                      <p className="text-text-light text-sm">{nextAppointment.doctor?.specialization || 'Spesialisasi Tidak Diketahui'}</p>
                    </div>
                  </div>
                  <div className="mt-3 pt-3 border-t border-primary/10">
                    <p className="text-sm text-text-light">
                      <Clock className="inline h-4 w-4 mr-1" />
                      {new Date(nextAppointment.queueDate).toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })} at {nextAppointment.appointmentTime}
                    </p>
                    <p className="text-sm text-text-light">
                      Queue Number: <span className="font-medium text-primary">{nextAppointment.queueNumber}</span>
                    </p>
                  </div>
                </div>
              ) : (
                <p className="text-text-light text-center py-4">Tidak ada janji temu mendatang.</p>
              )}
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
