// apps/admin/frontend/src/services/api.ts

import axios, { type AxiosInstance, type AxiosResponse, type AxiosError } from "axios"
import toast from "react-hot-toast"
// Import type-tipe dari ../types (yang sudah saya perbaiki di atas)
import type { User, AuthResponse, LoginCredentials, ApiResponse } from "../types" 

// =========================================================
// INTERFACES GLOBAL DAN UTAMA (Admin Frontend)
// Urutan penting untuk referensi antar interface

// 1. Interfaces Dokter (dasar untuk referensi di Polyclinic dan Schedule)
export interface DoctorDataFromAdminAPI { 
  _id: string; // MongoDB ID
  name: string;
  specialization: string;
  employeeId?: string;
  phone?: string;
  email?: string;
  licenseNumber?: string;
  joinDate?: string; 
  status?: 'Active' | 'On Leave' | 'Inactive'; 
  isActive?: boolean; 
}

export interface DoctorListApiResponse {
  success: boolean;
  data: DoctorDataFromAdminAPI[];
  pagination: { currentPage: number; totalPages: number; total: number; };
}

// 2. Interfaces Poliklinik (digunakan di Schedule dan mungkin Patient)
export interface PolyclinicData {
  _id: string;
  polyclinicId: string;
  name: string; 
  department: string; 
  description?: string;
  operatingHours?: {
    [day: string]: { start?: string; end?: string; isOpen?: boolean };
  };
  capacity?: {
    maxPatientsPerDay?: number;
    maxPatientsPerHour?: number;
  };
  facilities?: string[];
  location?: {
    building?: string;
    floor?: string;
    room?: string;
  };
  assignedDoctors?: Array<{
    _id?: string; 
    doctorId?: string | DoctorDataFromAdminAPI; // DoctorDataFromAdminAPI sudah dikenal
    schedule?: Array<{ day?: string; startTime?: string; endTime?: string; }>;
  }>;
  status: 'Active' | 'Maintenance' | 'Closed';
  stats?: {
    monthlyVisits: number;
    activeDoctors: number;
  };
  price?: number; 
}

export interface PolyclinicsApiResponse {
  success: boolean;
  data: PolyclinicData[];
  pagination: { currentPage: number; totalPages: number; total: number };
}

export interface PolyclinicStatsData {
  totalServices: number; 
  activeToday: number; 
  averageWaitTime: number; 
}

export interface PolyclinicStatsApiResponse {
  success: boolean;
  data: PolyclinicStatsData;
}


// 3. Interfaces Pasien
export interface PatientData { 
  _id: string; // _id adalah wajib jika PatientData merepresentasikan data dari DB
  patientId?: string; 
  fullName: string; 
  nik?: string; 
  dateOfBirth: string;
  gender: 'Laki-laki' | 'Perempuan';
  phone: string;
  email?: string; 
  address?: {
    street?: string;
    city?: string;
    province?: string;
    postalCode?: string;
  };
  bloodType?: 'A+' | 'A-' | 'B+' | 'B-' | 'AB+' | 'AB-' | 'O+' | 'O-';
  allergies?: string[];
  emergencyContact?: {
    name?: string;
    relationship?: string;
    phone?: string;
  };
  registrationDate?: string; 
  status?: 'Active' | 'Inactive'; 
  lastVisit?: string;
  age?: number;
  recentVisits?: any[]; 
  lifetimeValue?: number;
  createdAt?: string; 
  updatedAt?: string; 
}

export interface PatientStatsData {
  total: number;
  active: number;
  new: number;
  genderStats: Array<{ _id: string; count: number }>;
}

export interface PatientsApiResponse {
  success: boolean;
  data: PatientData[]; 
  pagination: { currentPage: number; totalPages: number; total: number }; 
}

export interface PatientStatsApiResponse {
  success: boolean;
  data: PatientStatsData;
}


// 4. Interfaces Jadwal
export interface ScheduleData {
  _id: string; 
  scheduleId: string; 
  doctorId: string | DoctorDataFromAdminAPI; 
  polyclinicId: string | PolyclinicData; 
  date: string; 
  startTime: string; 
  endTime: string; 
  totalSlots: number;
  bookedSlots: number;
  availableSlots: number;
  status: 'Active' | 'Cancelled' | 'Completed';
  notes?: string;
  estimatedWaitTime?: number;
  createdAt?: string;
  updatedAt?: string;
  doctorName?: string; 
  doctorSpecialization?: string; 
  polyclinicName?: string; 
  queues?: Array<{ 
    _id: string;
    patientId: { fullName: string; patientId?: string } | null;
    queueNumber: number;
    status: string;
  }>;
}

export interface ScheduleApiResponse {
  success: boolean;
  data: ScheduleData[];
  pagination: { currentPage: number; totalPages: number; total: number };
}

export interface SingleScheduleApiResponse {
  success: boolean;
  data: ScheduleData; 
}

export interface ScheduleStatsResponse {
  success: boolean;
  data: {
    totalSchedules: number;
    activeSchedules: number;
    totalSlotsToday: number;
    bookedSlotsToday: number;
    availableSlotsToday: number;
    utilizationToday: number; 
    doctorsOnDutyToday: number;
    newAppointmentsToday: number;
    averageWaitTimeToday: number;
  }
}

// 5. Interfaces Inventaris
export interface InventoryItemData { 
  _id: string; 
  itemId: string; 
  name: string;
  category: string;
  currentStock: number;
  minimumStock: number;
  unit: string;
  unitPrice: number;
  status: 'Available' | 'Low Stock' | 'Out of Stock';
  lastRestockDate?: string; 
  createdAt?: string;
  updatedAt?: string;
}

export interface InventoryStatsData {
  total: number; 
  lowStock: number;
  outOfStock: number;
  totalValue: number;
}

export interface InventoryApiResponse {
  success: boolean;
  data: InventoryItemData[]; 
  pagination: { currentPage: number; totalPages: number; total: number };
}

export interface InventoryStatsApiResponse {
  success: boolean;
  data: InventoryStatsData;
}


// 6. Interfaces untuk Laporan & Analisis
export interface ChartDataForRecharts { 
  name: string; 
  value: number; 
  [key: string]: any; 
}

export interface FinancialSummaryData {
  totalRevenue: number;
  operationalCost: number;
  profitMargin: number;
  patientSatisfaction: number;
}

export interface ServiceDistributionData { 
  name: string;
  value: number;
  color?: string;
}

export interface DashboardOverviewApiData {
  totalVisits: number;
  averageDaily: number;
  occupancyRate: number;
  totalPatients?: number; 
  erAdmissions?: number; 
  bloodUnitsOminus?: number; 
  availableBeds?: number; 
  patientTrendData?: ChartDataForRecharts[]; 
  patientPerHourData?: ChartDataForRecharts[]; 
  aiInsightSummary?: string; 
  aiRecommendations?: { id: string; text: string; priority: "high" | "medium" | "low" }[]; 
  todayAbsorption?: { 
    bloodOminus?: number; 
    patientsIncreased?: number; 
    nightShiftNurses?: number; 
  }
}


// 7. Interfaces untuk Appointment dan MedicalRecord (jika digunakan di Admin Dashboard)
// Ini adalah definisi yang relevan untuk Admin Dashboard, bukan web frontend.
// Sesuaikan jika Anda punya Appointment/MedicalRecord di web/types/index.ts yang berbeda
export interface Appointment {
  _id: string; 
  queueId: string; 
  patientId: string; 
  doctorId: string; 
  polyclinicId: string; 
  scheduleId: string; 
  queueNumber: number; 
  queueDate: string; 
  appointmentTime?: string; 
  status: 'Waiting' | 'In Progress' | 'Completed' | 'Cancelled' | 'No Show'; 
  priority: 'Normal' | 'Urgent' | 'Emergency'; 
  notes?: string;
  complaints?: string;
  createdAt?: string;
  updatedAt?: string;
  doctor?: { _id: string; name: string; specialization: string }; 
  polyclinic?: { _id: string; name: string };
}

export interface MedicalRecord {
  id: string; 
  patientId: string;
  visitDate: string;
  doctorName: string;
  diagnosis: string;
  treatments: string[];
  prescriptions: string[];
  labResults?: { 
    bloodPressure?: string;
    heartRate?: string;
    temperature?: string;
    weight?: string;
  };
  notes?: string; 
}


// =========================================================
// AXIOS INSTANCE
const api: AxiosInstance = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:5000/api/v1", 
  timeout: 10000,
  headers: {
    "Content-Type": "application/json",
  },
})

api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token")
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }
    return config
  },
  (error) => {
    return Promise.reject(error)
  },
)

api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error: AxiosError) => {
    const message = (error.response?.data as any)?.message || error.message || "An error occurred"
    if (error.response?.status === 401) {
      localStorage.removeItem("token")
      localStorage.removeItem("user")
      if (window.location.pathname !== '/login') {
        window.location.href = "/login"
      }
      toast.error("Sesi berakhir. Silakan login kembali.")
    } else if (error.code !== "ERR_CANCELED") {
        toast.error(message)
    }
    return Promise.reject(error)
  },
)

// =========================================================
// API SERVICES
export const authAPI = {
  login: async (credentials: LoginCredentials): Promise<AuthResponse> => {
    const response = await api.post<AuthResponse>("/auth/admin/login", credentials) 
    return response.data
  },
  demoLogin: async (): Promise<AuthResponse> => {
    const response = await api.post<AuthResponse>("/auth/admin/demo-login") 
    return response.data
  },
  verifyToken: async (): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.get<ApiResponse<{ user: User }>>("/auth/admin/verify-token")
    return response.data;
  },
  updateProfile: async (userData: Partial<User>): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.put<ApiResponse<{ user: User }>>("/auth/admin/profile", userData)
    return response.data
  },
  getProfile: async (): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.get<ApiResponse<{ user: User }>>("/auth/admin/profile")
    return response.data
  },
}

export const patientAPI = { // patientAPI ini untuk Admin Dashboard
  getPatients: async (
    page: number = 1, 
    limit: number = 10, 
    search: string = "", 
    status: string = "all"
  ): Promise<PatientsApiResponse> => {
    const response = await api.get<PatientsApiResponse>("/patients", {
      params: { page, limit, search, status }
    });
    return response.data;
  },
  getPatientStats: async (): Promise<PatientStatsApiResponse> => {
    const response = await api.get<PatientStatsApiResponse>("/patients/stats");
    return response.data;
  },
  createPatient: async (patientData: Omit<PatientData, '_id' | 'patientId' | 'registrationDate' | 'status' | 'lastVisit' | 'age' | 'createdAt' | 'updatedAt'>): Promise<ApiResponse<PatientData>> => {
    const response = await api.post<ApiResponse<PatientData>>("/patients", patientData);
    return response.data;
  },
  updatePatient: async (patientId: string, patientData: Partial<PatientData>): Promise<ApiResponse<PatientData>> => { 
    const response = await api.put<ApiResponse<PatientData>>(`/patients/${patientId}`, patientData);
    return response.data;
  },
  deletePatient: async (patientId: string): Promise<ApiResponse<null>> => {
    const response = await api.delete<ApiResponse<null>>(`/patients/${patientId}`);
    return response.data;
  },
  getPatientById: async (patientId: string): Promise<ApiResponse<PatientData>> => {
    const response = await api.get<ApiResponse<PatientData>>(`/patients/${patientId}`);
    return response.data;
  }
}

export const dashboardAPI = {
  getOverview: async (period: string = '30d'): Promise<ApiResponse<DashboardOverviewApiData>> => { 
    const response = await api.get<ApiResponse<DashboardOverviewApiData>>("/dashboard/overview", { params: { period } });
    return response.data;
  },
  getChartData: async (type: string = 'weekly-patients', period: string = '30d'): Promise<ApiResponse<ChartDataForRecharts[]>> => { 
    const response = await api.get<ApiResponse<ChartDataForRecharts[]>>(`/dashboard/charts?type=${type}`, { params: { period } });
    return response.data;
  },
  getFinancialSummary: async (period: string = '30d'): Promise<ApiResponse<FinancialSummaryData>> => { 
    const response = await api.get<ApiResponse<FinancialSummaryData>>("/dashboard/stats/financial", { params: { period } });
    return response.data;
  },
  getServiceDistribution: async (period: string = '30d'): Promise<ApiResponse<ServiceDistributionData[]>> => { 
    const response = await api.get<ApiResponse<ServiceDistributionData[]>>("/dashboard/stats/service-distribution", { params: { period } });
    return response.data;
  },
  // Endpoint baru untuk Dashboard Admin overview yang lebih spesifik (sesuai desain)
  getAdminDashboardOverview: async (): Promise<ApiResponse<DashboardOverviewApiData>> => {
    const response = await api.get<ApiResponse<DashboardOverviewApiData>>("/dashboard/admin-overview"); // Perlu endpoint di backend
    return response.data;
  },
  getPatientsPerWeek: async (): Promise<ApiResponse<ChartDataForRecharts[]>> => {
    const response = await api.get<ApiResponse<ChartDataForRecharts[]>>("/dashboard/admin-charts/patients-per-week"); // Perlu endpoint di backend
    return response.data;
  },
  getPatientsPerHour: async (): Promise<ApiResponse<ChartDataForRecharts[]>> => {
    const response = await api.get<ApiResponse<ChartDataForRecharts[]>>("/dashboard/admin-charts/patients-per-hour"); // Perlu endpoint di backend
    return response.data;
  },
  getAIInsights: async (): Promise<ApiResponse<{ summary: string; recommendations: { id: string; text: string; priority: "high" | "medium" | "low" }[] }>> => {
    const response = await api.get<ApiResponse<{ summary: string; recommendations: { id: string; text: string; priority: "high" | "medium" | "low" }[] }>>("/dashboard/ai-insights"); // Perlu endpoint di backend
    return response.data;
  },
  getSystemHealth: async (): Promise<ApiResponse<any>> => { // Ubah any ke tipe spesifik jika ada
    const response = await api.get<ApiResponse<any>>("/dashboard/system-health"); // Ini adalah endpoint yang sudah ada di routes/dashboardRoutes.ts
    return response.data;
