// apps/admin/frontend/src/services/api.ts

import axios, { type AxiosInstance, type AxiosResponse, type AxiosError } from "axios"
import toast from "react-hot-toast"
import type {
  ApiResponse,
  User, // Pastikan User di sini merujuk ke tipe user admin/staf
  AuthResponse,
  LoginCredentials,
} from "../types" 

// Buat instance axios
const api: AxiosInstance = axios.create({
  // Pastikan baseURL mengarah ke API Anda, biasanya port 5000 untuk backend
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:5000/api/v1", 
  timeout: 10000,
  headers: {
    "Content-Type": "application/json",
  },
})

// Request interceptor untuk menambahkan token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token")
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }
    return config
  },
  (error) => {
    return Promise.reject(error)
  },
)

// Response interceptor untuk menangani error global
api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error: AxiosError) => {
    const message = (error.response?.data as any)?.message || error.message || "An error occurred"
    if (error.response?.status === 401) {
      localStorage.removeItem("token")
      localStorage.removeItem("user")
      if (window.location.pathname !== '/login') {
        window.location.href = "/login"
      }
      toast.error("Sesi berakhir. Silakan login kembali.")
    } else if (error.code !== "ERR_CANCELED") {
        toast.error(message)
    }
    return Promise.reject(error)
  },
)

// Auth API
export const authAPI = {
  // PERUBAHAN DI SINI: Ubah endpoint login untuk admin
  login: async (credentials: LoginCredentials): Promise<AuthResponse> => {
    const response = await api.post<AuthResponse>("/auth/admin/login", credentials) // Endpoint baru
    return response.data
  },
  // PERUBAHAN DI SINI: Ubah endpoint demoLogin untuk admin
  demoLogin: async (): Promise<AuthResponse> => {
    const response = await api.post<AuthResponse>("/auth/admin/demo-login") // Endpoint baru
    return response.data
  },
  verifyToken: async (): Promise<ApiResponse<{ user: User }>> => {
    // Pastikan ini juga mengarah ke endpoint verify-token admin
    const response = await api.get<ApiResponse<{ user: User }>>("/auth/admin/verify-token")
    return response.data
  },
  updateProfile: async (userData: Partial<User>): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.put<ApiResponse<{ user: User }>>("/auth/admin/profile", userData)
    return response.data
  },
  getProfile: async (): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.get<ApiResponse<{ user: User }>>("/auth/admin/profile")
    return response.data
  },
}

// PERBAIKAN: Menambahkan objek dashboardAPI yang hilang (sudah ada di kode Anda)
export const dashboardAPI = {
  getOverview: async (): Promise<ApiResponse<any>> => {
    const response = await api.get<ApiResponse<any>>("/dashboard/overview")
    return response.data
  },
  getChartData: async (type: string): Promise<ApiResponse<any>> => {
    const response = await api.get<ApiResponse<any>>(`/dashboard/charts?type=${type}`)
    return response.data
  },
}

export default api
