// apps/admin/frontend/src/components/Button.tsx

import React from 'react';
import { cva, type VariantProps } from 'class-variance-authority'; 

const buttonVariants = cva(
  "inline-flex items-center justify-center rounded-lg text-sm font-semibold transition-colors duration-200",
  {
    variants: {
      variant: {
        primary: "bg-blue-600 text-white shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2",
        secondary: "bg-slate-100 text-slate-700 hover:bg-slate-200 focus:outline-none focus:ring-2 focus:ring-slate-500 focus:ring-offset-2",
        outline: "border border-slate-300 bg-white text-slate-700 hover:bg-slate-50 focus:outline-none focus:ring-2 focus:ring-slate-500 focus:ring-offset-2",
        ghost: "text-slate-600 hover:bg-slate-100 hover:text-slate-900 focus:outline-none focus:ring-2 focus:ring-slate-500 focus:ring-offset-2",
        danger: "bg-red-600 text-white shadow-sm hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2",
      },
      size: {
        sm: "h-8 px-3 py-1.5",
        md: "h-10 px-4 py-2",
        lg: "h-12 px-5 py-2.5",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "primary",
      size: "md",
    },
  }
);

// PERBAIKAN: Gunakan tipe props yang lebih komprehensif
// Gabungkan atribut HTMLButtonElement dan HTMLDivElement
type HtmlElementProps = React.ButtonHTMLAttributes<HTMLButtonElement> & React.HTMLAttributes<HTMLDivElement>;

interface ButtonProps extends HtmlElementProps, VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

// PERBAIKAN: Forward ref ke HTMLButtonElement atau HTMLDivElement
const Button = React.forwardRef<HTMLButtonElement | HTMLDivElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    // Tipekan Comp sebagai React.ElementType agar fleksibel
    const Comp: React.ElementType = asChild ? "div" : "button"; 

    return (
      <Comp
        className={buttonVariants({ variant, size, className })}
        // PERBAIKAN: Cast ref ke tipe yang lebih umum jika diperlukan
        ref={ref} // ref sudah menangani union type, tidak perlu as any lagi
        {...props} // Gunakan langsung props, TypeSript akan mengurus sisanya
      />
    );
  }
);
Button.displayName = "Button";

export default Button;
