// apps/admin/frontend/src/services/api.ts

import axios, { type AxiosInstance, type AxiosResponse, type AxiosError } from "axios"
import toast from "react-hot-toast"
import type { User, AuthResponse, LoginCredentials, ChangePasswordData, ApiResponse } from "../types" 

// ... (semua interface yang lain tidak berubah)
// INTERFACES ...
export interface DoctorDataFromAdminAPI { _id: string; name: string; specialization: string; employeeId?: string; phone?: string; email?: string; licenseNumber?: string; joinDate?: string; status?: 'Active' | 'On Leave' | 'Inactive'; isActive?: boolean; }
export interface DoctorListApiResponse { success: boolean; data: DoctorDataFromAdminAPI[]; pagination: { currentPage: number; totalPages: number; total: number; }; }
export interface PolyclinicData { _id: string; polyclinicId: string; name: string; department: string; description?: string; operatingHours?: { [day: string]: { start?: string; end?: string; isOpen?: boolean }; }; capacity?: { maxPatientsPerDay?: number; maxPatientsPerHour?: number; }; facilities?: string[]; location?: { building?: string; floor?: string; room?: string; }; assignedDoctors?: Array<{ _id?: string; doctorId?: string | DoctorDataFromAdminAPI; schedule?: Array<{ day?: string; startTime?: string; endTime?: string; }>; }>; status: 'Active' | 'Maintenance' | 'Closed'; stats?: { monthlyVisits: number; activeDoctors: number; }; price?: number; }
export interface PolyclinicsApiResponse { success: boolean; data: PolyclinicData[]; pagination: { currentPage: number; totalPages: number; total: number }; }
export interface PolyclinicStatsData { totalServices: number; activeToday: number; averageWaitTime: number; }
export interface PolyclinicStatsApiResponse { success: boolean; data: PolyclinicStatsData; }
export interface PatientData { _id: string; patientId?: string; fullName: string; nik?: string; dateOfBirth: string; gender: 'Laki-laki' | 'Perempuan'; phone: string; email?: string; address?: { street?: string; city?: string; province?: string; postalCode?: string; }; bloodType?: 'A+' | 'A-' | 'B+' | 'B-' | 'AB+' | 'AB-' | 'O+' | 'O-'; allergies?: string[]; emergencyContact?: { name?: string; relationship?: string; phone?: string; }; registrationDate?: string; status?: 'Active' | 'Inactive'; lastVisit?: string; age?: number; recentVisits?: any[]; lifetimeValue?: number; createdAt?: string; updatedAt?: string; }
export interface PatientStatsData { total: number; active: number; new: number; genderStats: Array<{ _id: string; count: number }>; }
export interface PatientsApiResponse { success: boolean; data: PatientData[]; pagination: { currentPage: number; totalPages: number; total: number }; }
export interface PatientStatsApiResponse { success: boolean; data: PatientStatsData; }
export interface ScheduleData { _id: string; scheduleId: string; doctorId: string | DoctorDataFromAdminAPI; polyclinicId: string | PolyclinicData; date: string; startTime: string; endTime: string; totalSlots: number; bookedSlots: number; availableSlots: number; status: 'Active' | 'Cancelled' | 'Completed'; notes?: string; estimatedWaitTime?: number; createdAt?: string; updatedAt?: string; doctorName?: string; doctorSpecialization?: string; polyclinicName?: string; queues?: Array<{ _id: string; patientId: { fullName: string; patientId?: string } | null; queueNumber: number; status: string; }>; }
export interface ScheduleApiResponse { success: boolean; data: ScheduleData[]; pagination: { currentPage: number; totalPages: number; total: number }; }
export interface SingleScheduleApiResponse { success: boolean; data: ScheduleData; }
export interface ScheduleStatsResponse { success: boolean; data: { totalSchedules: number; activeSchedules: number; totalSlotsToday: number; bookedSlotsToday: number; availableSlotsToday: number; utilizationToday: number; doctorsOnDutyToday: number; newAppointmentsToday: number; averageWaitTimeToday: number; } }
export interface InventoryItemData { _id: string; itemId: string; name: string; category: string; currentStock: number; minimumStock: number; unit: string; unitPrice: number; status: 'Available' | 'Low Stock' | 'Out of Stock'; lastRestockDate?: string; createdAt?: string; updatedAt?: string; }
export interface InventoryStatsData { total: number; lowStock: number; outOfStock: number; totalValue: number; }
export interface InventoryApiResponse { success: boolean; data: InventoryItemData[]; pagination: { currentPage: number; totalPages: number; total: number }; }
export interface InventoryStatsApiResponse { success: boolean; data: InventoryStatsData; }
export interface ChartDataForRecharts { name: string; value: number; [key: string]: any; }
export interface FinancialSummaryData { totalRevenue: number; operationalCost: number; profitMargin: number; patientSatisfaction: number; }
export interface ServiceDistributionData { name: string; value: number; color?: string; }
export interface DashboardOverviewApiData { totalVisits?: number; averageDaily?: number; occupancyRate?: number; totalPatients?: number; erAdmissions?: number; bloodUnitsOminus?: number; availableBeds?: number; patientTrendData?: ChartDataForRecharts[]; patientPerHourData?: ChartDataForRecharts[]; aiInsightSummary?: string; aiRecommendations?: { id: string; text: string; priority: "high" | "medium" | "low" }[]; todayAbsorption?: { bloodOminus?: number; patientsIncreased?: number; nightShiftNurses?: number; } }
export interface Appointment { _id: string; queueId: string; patientId: string; doctorId: string; polyclinicId: string; scheduleId: string; queueNumber: number; queueDate: string; appointmentTime?: string; status: 'Waiting' | 'In Progress' | 'Completed' | 'Cancelled' | 'No Show'; priority: 'Normal' | 'Urgent' | 'Emergency'; notes?: string; complaints?: string; createdAt?: string; updatedAt?: string; doctor?: { _id: string; name: string; specialization: string }; polyclinic?: { _id: string; name: string }; }
export interface MedicalRecord { id: string; patientId: string; visitDate: string; doctorName: string; diagnosis: string; treatments: string[]; prescriptions: string[]; labResults?: { bloodPressure?: string; heartRate?: string; temperature?: string; weight?: string; }; notes?: string; }

const api: AxiosInstance = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:5000/api/v1", 
  timeout: 10000,
  headers: { "Content-Type": "application/json" },
})

api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token")
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }
    return config
  },
  (error) => {
    return Promise.reject(error)
  },
)

api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error: AxiosError) => {
    const message = (error.response?.data as any)?.message || error.message || "An error occurred"
    if (error.response?.status === 401) {
      localStorage.removeItem("token")
      localStorage.removeItem("user")
      if (window.location.pathname !== '/login') {
        window.location.href = "/login"
      }
      toast.error("Sesi berakhir. Silakan login kembali.")
    } else if (error.code !== "ERR_CANCELED") {
        toast.error(message)
    }
    return Promise.reject(error)
  },
)

// =========================================================
// API SERVICES
export const authAPI = {
  login: async (credentials: LoginCredentials): Promise<AuthResponse> => {
    const response = await api.post<AuthResponse>("/auth/admin/login", credentials) 
    return response.data
  },
  demoLogin: async (): Promise<AuthResponse> => {
    const response = await api.post<AuthResponse>("/auth/admin/demo-login") 
    return response.data
  },
  verifyToken: async (): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.get<ApiResponse<{ user: User }>>("/auth/admin/verify-token")
    return response.data;
  },
  updateProfile: async (userData: Partial<User>): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.put<ApiResponse<{ user: User }>>("/auth/admin/profile", userData)
    return response.data;
  },
  getProfile: async (): Promise<ApiResponse<{ user: User }>> => {
    const response = await api.get<ApiResponse<{ user: User }>>("/auth/admin/profile")
    return response.data;
  },
  changePassword: async (passwordData: ChangePasswordData): Promise<ApiResponse<null>> => {
    const response = await api.put<ApiResponse<null>>("/auth/admin/change-password", passwordData);
    return response.data;
  }
}

// ... (patientAPI tidak berubah)
export const patientAPI = { getPatients: async (page: number = 1, limit: number = 10, search: string = "", status: string = "all"): Promise<PatientsApiResponse> => { const response = await api.get<PatientsApiResponse>("/patients", { params: { page, limit, search, status } }); return response.data; }, getPatientStats: async (): Promise<PatientStatsApiResponse> => { const response = await api.get<PatientStatsApiResponse>("/patients/stats"); return response.data; }, createPatient: async (patientData: Omit<PatientData, '_id' | 'patientId' | 'registrationDate' | 'status' | 'lastVisit' | 'age' | 'createdAt' | 'updatedAt'>): Promise<ApiResponse<PatientData>> => { const response = await api.post<ApiResponse<PatientData>>("/patients", patientData); return response.data; }, updatePatient: async (patientId: string, patientData: Partial<PatientData>): Promise<ApiResponse<PatientData>> => { const response = await api.put<ApiResponse<PatientData>>(`/patients/${patientId}`, patientData); return response.data; }, deletePatient: async (patientId: string): Promise<ApiResponse<null>> => { const response = await api.delete<ApiResponse<null>>(`/patients/${patientId}`); return response.data; }, getPatientById: async (patientId: string): Promise<ApiResponse<PatientData>> => { const response = await api.get<ApiResponse<PatientData>>(`/patients/${patientId}`); return response.data; } }

// ✅ PERBAIKAN: dashboardAPI disesuaikan dengan rute baru di backend
export const dashboardAPI = {
  // --- Rute untuk dashboard admin yang baru ---
  getAdminDashboardOverview: async (): Promise<ApiResponse<DashboardOverviewApiData>> => {
    const response = await api.get<ApiResponse<DashboardOverviewApiData>>("/dashboard/admin-overview"); 
    return response.data;
  },
  getPatientsPerWeek: async (): Promise<ApiResponse<ChartDataForRecharts[]>> => {
    const response = await api.get<ApiResponse<ChartDataForRecharts[]>>("/dashboard/admin-charts/patients-per-week"); 
    return response.data;
  },
  getPatientsPerHour: async (): Promise<ApiResponse<ChartDataForRecharts[]>> => {
    const response = await api.get<ApiResponse<ChartDataForRecharts[]>>("/dashboard/admin-charts/patients-per-hour"); 
    return response.data;
  },
  getAIInsights: async (): Promise<ApiResponse<{ summary: string; recommendations: { id: string; text: string; priority: "high" | "medium" | "low" }[] }>> => {
    const response = await api.get<ApiResponse<{ summary: string; recommendations: { id: string; text: string; priority: "high" | "medium" | "low" }[] }>>("/dashboard/admin-charts/ai-insights"); 
    return response.data;
  },
  getSystemHealth: async (): Promise<ApiResponse<any>> => { 
    const response = await api.get<ApiResponse<any>>("/dashboard/system/health"); // URL sudah benar
    return response.data;
  },

  // --- Rute dashboard umum yang sudah ada ---
  getOverview: async (period: string = '30d'): Promise<ApiResponse<DashboardOverviewApiData>> => { 
    const response = await api.get<ApiResponse<DashboardOverviewApiData>>("/dashboard/overview", { params: { period } });
    return response.data;
  },
  getChartData: async (type: string = 'weekly-patients', period: string = '30d'): Promise<ApiResponse<ChartDataForRecharts[]>> => { 
    const response = await api.get<ApiResponse<ChartDataForRecharts[]>>(`/dashboard/charts?type=${type}`, { params: { period } });
    return response.data;
  },
  getFinancialSummary: async (period: string = '30d'): Promise<ApiResponse<FinancialSummaryData>> => { 
    const response = await api.get<ApiResponse<FinancialSummaryData>>("/dashboard/stats/financial", { params: { period } });
    return response.data;
  },
  getServiceDistribution: async (period: string = '30d'): Promise<ApiResponse<ServiceDistributionData[]>> => { 
    const response = await api.get<ApiResponse<ServiceDistributionData[]>>("/dashboard/stats/service-distribution", { params: { period } });
    return response.data;
  },
}

