"use client"

import type React from "react"
import { useState } from "react"
import { useNavigate, useLocation } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext.ts"
import { Eye, EyeOff, Mail, Lock, AlertCircle, Loader2 } from "lucide-react"
import toast from "react-hot-toast"

const Login: React.FC = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  })
  const [showPassword, setShowPassword] = useState(false)
  const [isLoading, setIsLoading] = useState(false)

  const { login } = useAuth()
  const navigate = useNavigate()
  const location = useLocation()

  const from = location.state?.from?.pathname || "/dashboard"
  const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api"

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (isLoading) return;
    setIsLoading(true)

    try {
      console.log('Attempting login with:', { email: formData.email });
      
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      })

      const data = await response.json()
      console.log('Login response:', { status: response.status, data });

      if (response.ok && data.success) {
        // Pastikan token tersimpan dengan benar
        if (data.data?.token) {
          localStorage.setItem('token', data.data.token);
          localStorage.setItem('user', JSON.stringify(data.data.user));
          
          // Verifikasi token tersimpan
          const savedToken = localStorage.getItem('token');
          console.log('Token saved:', savedToken ? 'Yes' : 'No');
          
          toast.success("Login berhasil!")
          
          // Panggil login dari konteks
          await login(data.data)
          
          // Tambahkan delay kecil sebelum navigate
          setTimeout(() => {
            navigate(from, { replace: true })
          }, 100);
        } else {
          toast.error("Token tidak ditemukan dalam respons server")
        }
      } else {
        toast.error(data.message || "Email atau password salah")
      }
    } catch (err) {
      console.error("Login error:", err)
      toast.error("Tidak dapat terhubung ke server.")
    } finally {
      setIsLoading(false)
    }
  }

  const handleDemoLogin = async () => {
    if (isLoading) return;
    setIsLoading(true)

    try {
      console.log('Attempting demo login');
      
      const response = await fetch(`${API_BASE_URL}/auth/demo-login`, {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
      });

      const data = await response.json();
      console.log('Demo login response:', { status: response.status, data });
      
      if(response.ok && data.success) {
        // Pastikan token tersimpan dengan benar
        if (data.data?.token) {
          localStorage.setItem('token', data.data.token);
          localStorage.setItem('user', JSON.stringify(data.data.user));
          
          // Verifikasi token tersimpan
          const savedToken = localStorage.getItem('token');
          console.log('Demo token saved:', savedToken ? 'Yes' : 'No');
          
          toast.success('Login demo berhasil!');
          
          // Panggil login dari konteks
          await login(data.data);
          
          // Tambahkan delay kecil sebelum navigate
          setTimeout(() => {
            navigate('/dashboard', { replace: true });
          }, 100);
        } else {
          toast.error("Token tidak ditemukan dalam respons server")
        }
      } else {
        toast.error(data.message || "Gagal melakukan login demo.");
      }
    } catch (err) {
      console.error("Demo login error:", err)
      toast.error("Tidak dapat terhubung ke server untuk login demo.");
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-600 rounded-2xl mb-4">
            <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4"
              />
            </svg>
          </div>
          <h1 className="text-3xl font-bold text-slate-800 mb-2">Masuk ke Dashboard</h1>
          <p className="text-slate-600">Silakan masuk dengan akun administrator Anda</p>
        </div>

        {/* Login Form */}
        <div className="bg-white rounded-2xl shadow-xl border border-slate-200 p-8">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Email Field */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-slate-700 mb-2">
                Email
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Mail className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="email"
                  name="email"
                  type="email"
                  required
                  value={formData.email}
                  onChange={handleChange}
                  className="block w-full pl-10 pr-3 py-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  placeholder="admin@sehatify.com"
                />
              </div>
            </div>

            {/* Password Field */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-slate-700 mb-2">
                Password
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="password"
                  name="password"
                  type={showPassword ? "text" : "password"}
                  required
                  value={formData.password}
                  onChange={handleChange}
                  className="block w-full pl-10 pr-12 py-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  placeholder="••••••••"
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5 text-slate-400 hover:text-slate-600" />
                  ) : (
                    <Eye className="h-5 w-5 text-slate-400 hover:text-slate-600" />
                  )}
                </button>
              </div>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={isLoading}
              className="w-full flex justify-center items-center py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {isLoading ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Memproses...
                </>
              ) : (
                "Masuk ke Dashboard"
              )}
            </button>
          </form>

          {/* Demo Login */}
          <div className="mt-6 pt-6 border-t border-slate-200">
            <button
              onClick={handleDemoLogin}
              disabled={isLoading}
              className="w-full py-3 px-4 border border-slate-300 rounded-lg text-sm font-medium text-slate-700 bg-slate-50 hover:bg-slate-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-slate-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Demo Login (Admin)
            </button>
          </div>
        </div>
        <div className="text-center mt-8">
          <p className="text-sm text-slate-500">© 2025 Sehatify Hospital Management System</p>
        </div>
      </div>
    </div>
  )
}

export default Login
