import React from 'react';
import { useQuery } from '@tanstack/react-query';
import { getDashboardInsights, getWeeklyVisitStats } from '../services/api.ts';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Users, AlertTriangle, Droplets, BedDouble } from 'lucide-react';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

// Komponen untuk Kartu Metrik
const MetricCard: React.FC<{ title: string; value: string | number; icon: React.ElementType, color: string }> = ({ title, value, icon: Icon, color }) => (
  <div className="bg-white p-6 rounded-xl shadow-sm border border-slate-200 flex items-center justify-between">
    <div>
      <p className="text-sm font-semibold text-slate-500 uppercase">{title}</p>
      <p className="text-3xl font-bold text-slate-800">{value}</p>
    </div>
    <div className={`p-3 rounded-full ${color}`}>
      <Icon className="text-white" size={24} />
    </div>
  </div>
);

const Dashboard: React.FC = () => {
  // Query untuk data utama dasbor (kartu & antrian dari AI)
  const { data: insightsData, isLoading: insightsLoading, error: insightsError } = useQuery({
    queryKey: ['dashboardInsights'],
    queryFn: getDashboardInsights,
  });

  // Query untuk data grafik pasien mingguan
  const { data: statsData, isLoading: statsLoading, error: statsError } = useQuery({
    queryKey: ['weeklyVisitStats'],
    queryFn: getWeeklyVisitStats,
  });

  // Konfigurasi untuk Grafik
  const lineChartData = {
    labels: statsData?.data.labels || ['Sen', 'Sel', 'Rab', 'Kam', 'Jum', 'Sab', 'Min'],
    datasets: [
      {
        label: 'Pasien',
        data: statsData?.data.values || [],
        fill: true,
        backgroundColor: 'rgba(59, 130, 246, 0.1)',
        borderColor: 'rgb(59, 130, 246)',
        tension: 0.4,
        pointBackgroundColor: 'rgb(59, 130, 246)',
      },
    ],
  };

  const lineChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: { legend: { display: false } },
    scales: { x: { grid: { display: false } } }
  };

  if (insightsLoading || statsLoading) {
    return <div className="text-center p-8">Memuat data dasbor...</div>;
  }

  if (insightsError || statsError) {
    return <div className="text-center p-8 text-red-500">Gagal memuat data dasbor. Coba muat ulang halaman.</div>;
  }

  const metrics = insightsData?.data.metrics;
  const queueList = insightsData?.data.details?.queueList;

  return (
    <div className="space-y-8">
      {/* Bagian Atas: Kartu Metrik */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <MetricCard title="Total Pasien" value={metrics?.totalPatients || 0} icon={Users} color="bg-blue-500" />
        <MetricCard title="Gawat Darurat" value={metrics?.emergencyToday || 0} icon={AlertTriangle} color="bg-red-500" />
        <MetricCard title="Stok Kritis" value={metrics?.lowStockCount || 0} icon={Droplets} color="bg-yellow-500" />
        <MetricCard title="Bed Tersedia" value={metrics?.availableBeds || 0} icon={BedDouble} color="bg-green-500" />
      </div>

      {/* Bagian Tengah: Grafik dan Antrian */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Grafik Pasien Mingguan */}
        <div className="lg:col-span-2 bg-white p-6 rounded-xl shadow-sm border border-slate-200">
          <h3 className="font-bold text-lg text-slate-800 mb-4">Pasien Minggu Ini</h3>
          <div className="h-80">
            <Line options={lineChartOptions} data={lineChartData} />
          </div>
        </div>

        {/* Antrian Hari Ini */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-slate-200">
          <div className="flex justify-between items-center mb-4">
            <h3 className="font-bold text-lg text-slate-800">Antrian Hari Ini</h3>
            <span className="text-sm font-semibold text-blue-600">{queueList?.length || 0} Pasien</span>
          </div>
          <div className="space-y-4 overflow-y-auto max-h-80 pr-2">
            {queueList && queueList.length > 0 ? (
              queueList.map((item: any) => (
                <div key={item._id} className="flex items-center gap-4">
                  <div className="w-10 h-10 rounded-full bg-slate-200 flex items-center justify-center font-bold text-blue-600 flex-shrink-0">
                    {item.patientId?.name?.charAt(0) || 'P'}
                  </div>
                  <div className="flex-grow">
                    <p className="font-semibold text-slate-800 truncate">{item.patientId?.name || 'Pasien Terhapus'}</p>
                    <p className="text-xs text-slate-500">{item.polyclinicId?.name || 'Poli Terhapus'}</p>
                  </div>
                  <div className={`ml-auto text-xs font-bold px-2 py-1 rounded-full ${
                    item.status === 'Waiting' ? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'
                  }`}>
                    {item.status === 'Waiting' ? 'MENUNGGU' : 'DILAYANI'}
                  </div>
                </div>
              ))
            ) : (
              <p className="text-center text-slate-500 py-8">Tidak ada antrian hari ini.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
